
// Variable declaration
let sCity = $("#search-city");
let sButton = $("#search-button");
let cButton = $("#clear-history");
let cDate = $("#current-date");
let cCity = $("#current-city");
let cTemperature = $("#temperature");
let cWSpeed = $("#wind-speed");
let cHumidty= $("#humidity");
let cityList = [];
let city = "";
//API key generated by "api.openweathermap.org" 
let APIKey = "f0e3bf833207269b3a71b7a880f6ccab";

$(window).on('load', lastCitySearch)

// Finds the last city searched
function lastCitySearch() {
    cityList = JSON.parse(localStorage.getItem("cName")); //check if there is a file named cName on the local drive, if yes is going to load on the variable cityList
    if (cityList !== null) {
        for (i = 0; i < cityList.length; i++) {
            aTList(cityList[i]);
        }
        city = cityList[i - 1];
        ctWeather(city);
    }
}


//List the cities searched
function aTList(city) {
    let lLine = $("<li>" + city.toUpperCase() + "</li>");
    $(lLine).attr("class", "list-group-item");//adds the class "list group item" to the lLine variable
    $(".list-group").append(lLine);//load the variable litEL to the index.html
}


function ctWeather(city) {
    // Here we build the URL so we can get a data from server side.
    let urlctWeather = "https://api.openweathermap.org/data/2.5/weather?q=" + city + "&APPID=" + APIKey + "&units=imperial"; //creates a varaible following the standsrs by opeanweathermap.org
    //ajax function which reads data from a web server
    $.ajax({ 
        url: urlctWeather,     
        method: "GET",
    }).then(function (response) {
        //Icon Property according to https://openweathermap.org/weather-conditions.
        let weathericon = response.weather[0].icon;//creates a variable to get the weather icon code according to wheathermap.org
        let iconurl = "https://openweathermap.org/img/wn/" + weathericon + "@2x.png"; //create variable to get create a url to get the weather icon
        // The date format method is taken from the  https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Date
        let aDate = new Date(response.dt * 1000).toLocaleDateString('en-US');//returns the current date

        //parse the response for name of city and concanatig the date and icon.
        $(cDate).html("Current Weather  " + aDate);
        $(cCity).html(response.name + "<img src=" + iconurl + ">");

        // parse the response to display the current temperature.
        let temp = response.main.temp;
        //Display the temperature
        $(cTemperature).html((temp).toFixed(2) + " &#8457");
         //Wind speed
         let wSpeed = response.wind.speed.toFixed(2);
        $(cWSpeed).html(wSpeed + " mph");
        //Humidity
        $(cHumidty).html(response.main.humidity + " %");
      
        fdayWForecast(response.id);
        
        //Save the City name on the searched cities list
        if (response.cod == 200) {
                if (cityList == null) {
                  cityList = [];                  
                }
                if (find(city) > 0) {
                    cityList.push(city.toUpperCase());
                    localStorage.setItem("cName", JSON.stringify(cityList));
                    aTList(city);
                }
        }
    });
}


//5 days forecast for the current city
function fdayWForecast(cityid) {
    let urlfdayWForecast = "https://api.openweathermap.org/data/2.5/forecast?id=" + cityid + "&appid=" + APIKey + "&units=imperial";
    $.ajax({
        url: urlfdayWForecast,
        method: "GET"
    }).then(function (response) {
        for (i = 0; i < 5; i++) {
            let date = new Date((response.list[((i + 1) * 8) - 1].dt) * 1000).toLocaleDateString('en-US');
            let iconcode = response.list[((i + 1) * 8) - 1].weather[0].icon;
            let iconurl = "https://openweathermap.org/img/wn/" + iconcode + ".png";
            let temp = response.list[((i + 1) * 8) - 1].main.temp.toFixed(2);
            let humidity = response.list[((i + 1) * 8) - 1].main.humidity;
            let wSpeed = response.list[((i + 1) * 8) - 1].wind.speed.toFixed(2);
            $("#fDate" + i).html(date);
            $("#fImg" + i).html("<img src=" + iconurl + ">");
            $("#fTemp" + i).html(temp + " &#8457");
            $("#fWind" + i).html(wSpeed + " mph");
            $("#fHumidity" + i).html(humidity + " %");
        }
    });
}


// Check if there is a city already searched on the city history
function find(city) {
    for (let i = 0; i < cityList.length; i++) {
        if (city.toUpperCase() === cityList[i]) {
            return 0;
        }
    }
    return 1;
}


$(sButton).on("click", searchWCity);

// Search the city typed on the input text box and then call "ctWeather" fucntion which displays the info about the city searched.
function searchWCity() {
    //event.preventDefault();
    if (sCity.val().trim() !== "") {
        city = sCity.val().trim();
        ctWeather(city);
    }
}


$(document).on("click", callctWeather);

// When clicked on any city of the search history it d
function callctWeather(event) {
    let lcButton = event.target;
    if (event.target.matches("li")) {
        city = lcButton.textContent.trim();
        ctWeather(city);
    }
}


$(cButton).on("click", cHistory);

//Clear the search history
function cHistory(event){
    cityList=[];
    localStorage.removeItem("cName");
    document.location.reload();
}
